IF DB_ID('MyCompany') IS NULL
	CREATE DATABASE MyCompany;
GO
USE MyCompany;
GO
IF NOT EXISTS( SELECT * FROM INFORMATION_SCHEMA.TABLES WHERE TABLE_NAME = 'Employees' )
	CREATE TABLE MyCompany.dbo.Employees
	(
		ID INT IDENTITY(1,1) PRIMARY KEY,
		FirstName VARCHAR(25) NOT NULL,
		LastName VARCHAR(25) NOT NULL,		
		Address VARCHAR(40) NOT NULL,
		City VARCHAR(25) NOT NULL,
		State VARCHAR(25) NOT NULL,
		Country VARCHAR(25) NOT NULL,
		JobTitle VARCHAR(40) NOT NULL,
		JobDepartment VARCHAR(25) NOT NULL,
		ManagerFirstName VARCHAR(25) NOT NULL,
		ManagerLastName VARCHAR(25) NOT NULL
	);
GO
GO